let n=document.getElementsByClassName("file-input");for(let a of n)a.addEventListener("change",function(){const e=this.id;if(this.files.length){const l=this.files[0].size*.001,i=this.dataset.max;if(l>i)alert(sizeValidationMessage.replace(":max",i)),this.value=null;else{document.getElementById(e+"Name").value=this.files[0].name,document.getElementById(e+"NameDisplay").innerHTML=this.files[0].name;let t=new FileReader;t.readAsDataURL(this.files[0]),t.onloadend=function(){let s=t.result;document.getElementById(e+"Base64").value=s}}}});
