new Vue({el:"#app",data(){return{name:"",detail:"",skill_name:"",skill_details:"",id:"",skill_id:"",isElementVisible:!0,skillValue:""}},methods:{async select(i){await fetch("/api/skill/"+i).then(l=>l.json()).then(l=>{this.skill_name=l.skill_name,this.skill_details=l.skill_details,this.id=l.id})},clear:function(i){this.skill_name="",this.skill_details="",this.id=""},async deleteSkill(i){await fetch("/api/skill/"+i).then(l=>l.json()).then(l=>{this.skill_id=i,this.skillValue=l.skill_name})}},created(){setTimeout(()=>this.isElementVisible=!1,3e3)},mounted(){}});
