new Vue({el:"#app",data(){return{baseURL:window.location.origin}},methods:{async followCompany(s){await axios.post("/companies/companies/"+s+"/follow").then(a=>{a.status==200&&window.location.reload()})},async react(s,a,t){const e=await fetch(this.baseURL+"/api/evaluations/react/"+a+"/"+t);if(e.status=="200"){const o=await e.json();this.$refs[t].textContent=o,this.$refs[t].parentElement.classList.toggle("active")}}}});
